package com.example.song.mycontroller;import android.app.Activity;import android.bluetooth.BluetoothAdapter;import android.bluetooth.BluetoothDevice;import android.content.Intent;import android.os.Bundle;import android.util.Log;import android.view.MotionEvent;import android.view.View;import android.widget.Button;import android.widget.ImageButton;import android.widget.ListAdapter;import android.widget.ListView;import com.example.song.mycontroller.Rocker;import java.util.Set;public class MainActivity extends Activity {    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        final Rocker rc_l=(Rocker)findViewById(R.id.left_rocker);        final Rocker rc_r=(Rocker)findViewById(R.id.right_rocker);        rc_l.starAnimate();        rc_r.starAnimate();        rc_l.setOnTouchListener(new View.OnTouchListener() {            @Override            public boolean onTouch(View v, MotionEvent event) {                switch (event.getAction() & MotionEvent.ACTION_MASK) {                    case MotionEvent.ACTION_MOVE:                        return false;                    case MotionEvent.ACTION_UP:                        rc_l.reCenter(true,true);                        return false;                };                return true;            }        });        rc_r.setOnTouchListener(new View.OnTouchListener() {            @Override            public boolean onTouch(View v, MotionEvent event) {                switch (event.getAction() & MotionEvent.ACTION_MASK) {                    case MotionEvent.ACTION_MOVE:                        return false;                    case MotionEvent.ACTION_UP:                        rc_r.reCenter(true,false);                        return false;                };                return true;            }        });        rc_r.setRawDataChange(new Rocker.OnDataChange(){            public void onChange(float rawx,float rawy){            };        });        ListView lv=(ListView)findViewById(R.id.listView);        ImageButton ib=(ImageButton)findViewById(R.id.connect);        final MyBluetooth mbt=new MyBluetooth();        ib.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                mbt.conectToRemoteDevice("00:11:06:03:02:27");            }        });    }}